const axios = require('axios');

// Fetch todos using axios
const response = await axios.get('https://jsonplaceholder.typicode.com/todos');

return response.data;

const { z } = require('zod');

// Define todo schema
const TodoSchema = z.object({
  userId: z.number(),
  id: z.number(),
  title: z.string(),
  completed: z.boolean()
});

// Validate each item individually
try {
  const validated = TodoSchema.parse($json);
  return {
    ...validated,
    _validated: true
  };
} catch (error) {
  return {
    ...$json,
    _validated: false,
    _error: error.message
  };
}

// Only pass through completed todos that are validated
if ($json._validated && $json.completed) {
  return $json;
}
return null;

// Pass through validated items with userId for aggregation
if ($json._validated) {
  return {
    userId: $json.userId,
    todoId: $json.id,
    title: $json.title
  };
}
return null;

// Create summary from validated item
if ($json._validated) {
  return {
    userId: $json.userId,
    id: $json.id,
    title: $json.title,
    completed: $json.completed,
    validationPassed: true
  };
}
return null;
